import Link from 'next/link';
import { jsx } from '@emotion/core';
import styled from 'styled-components';
/** @jsx jsx */

export default () => (
  <HeaderWrap> 
    <p css={{ margin: 0, fontSize: '2em' }}>My Blog</p>
    <p>Recipe</p>
    <p>About Me</p>
    <p>Contact</p>
    <Link href="/post/new" passHref>
      <a css={{ color: 'white', background: 'orange', cursor: 'pointer', padding: '0.5rem 1rem' }}>+ Add Post</a>
    </Link>

  </HeaderWrap>
);

const HeaderWrap = styled.div`
 background: white;
 width: 90%;
 margin: auto;
 display: flex;
 justify-content: space-between;
 align-items: center;
 padding: 1rem 0;

`
