import Link from "next/link";
import { jsx, Global, css } from "@emotion/core";
// import styled from 'styled-components';
/** @jsx jsx */

export default () => (
  <>
  <Global
      styles={
        css`
        .header {
          background: white;
          width: 90%;
          margin: auto;
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 1rem 0;
        }
        .logo {
          font-size: 2rem;
          margin: 0;
        }
        .links {
          display: flex;
          align-items: center;
          justify-content: space-between;
          width: 40%;
          margin-left: auto;
        }
        a {
          color: black;
          text-decoration: none;
        }
        p, a {
          cursor: pointer;
          transition: all 0.5s ease;
          &:hover {
            color: orange;
          }
        }
        `
      }
    />
  <header
    className = 'header'
    >
    <p className='logo'>
      My Blog
    </p>
    <div className='links'>
      <Link href="/post/new" passHref>
        <a>Add Post</a>
      </Link>
      <p>Recipe</p>
      <p>About Me</p>
      <p>Contact</p>
    </div>
  </header>
  </>
);

