import Link from 'next/link';
import { jsx } from '@emotion/core';

/** @jsx jsx */

export default () => (
  <header
    css={{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      margin: '1rem 0',
      background: '#252525',
      padding: '1rem',
      color: 'white'
    }}
  >
    <p css={{ margin: 0, fontSize: '2em' }}>My Blog</p>
    <Link href="/post/new" passHref>
      <a css={{ color: 'white', background: 'orange', cursor: 'pointer', padding: '0.5rem 1rem' }}>+ Add Post</a>
    </Link>
  </header>
);
