import Link from "next/link";
import { jsx, Global } from "@emotion/core";
// import styled from 'styled-components';
/** @jsx jsx */

export default () => (
  <>
  <Global
      styles={{
        '*': { boxSizing: 'border-box' },
        body: {
          margin: 0,
          background: 'white',
          color: 'hsl(200, 20%, 20%)',
          fontFamily: "'Rubik', sans-serif",
        },
        h2: {
          fontSize: '1em',
          textTransform: 'uppercase',
          color: 'hsl(200, 20%, 50%)',
        },
        '[disabled]': {
          cursor: 'not-allowed',
          opacity: 0.6,
        },
      }}
    />
  <header
    className = 'header'
    css={{
      background: "white",
      width: "90%",
      margin: "auto",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      padding: "1rem 0"
    }}
  >
    <p
      className="logo"
      css={{
        fontSize: "2rem",
        margin: "0"
      }}
    >
      My Blog
    </p>
    <div
      className="links"
      css={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        width: "40%",
        marginLeft: "auto"
      }}
    >
      <Link href="/post/new" passHref>
        <a>Add Post</a>
      </Link>
      <p>Recipe</p>
      <p>About Me</p>
      <p>Contact</p>
    </div>
  </header>
  </>
);

