import Link from 'next/link';
import { jsx } from '@emotion/core';
import styled from 'styled-components';
/** @jsx jsx */

export default () => (
  <HeaderWrap> 
    <p className='logo'>My Blog</p>
     <div className='links'>
     <Link href="/post/new" passHref >
      <a className = 'postLink'>Add Post</a> 
    </Link>
    <p>Recipe</p>
    <p>About Me</p>
    <p>Contact</p>

    </div>

  </HeaderWrap>
);

const HeaderWrap = styled.div`
 background: white;
 width: 90%;
 margin: auto;
 display: flex;
 justify-content: space-between;
 align-items: center;
 padding: 1rem 0;
 .logo {
   font-size: 2rem;
   margin: 0;
 }
 .links {
   display: flex;
   align-items: center;
   justify-content: space-between;
   width: 40%;
   margin-left: auto;
  //  a {
  //    text-decoration: none;
  //    color: orange;
  //  }
   p {
     cursor: pointer;
     transition: all 0.5s ease;
     &:hover {
       color: orange;
     }
   }
   .postLink {
     color: gray;
     text-decoration: none;
   }
 }
`
