import Link from "next/link";
import { jsx, Global, css } from "@emotion/core";
// import styled from 'styled-components';
/** @jsx jsx */

export default () => (
  <>
  <Global
      styles={
        css`
        .header {
          background: white;
          width: 90%;
          margin: auto;
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 1rem 0;
        }
        @media(max-width: 500px) {
          width: 100%;
        }
        `
      }
    />
  <header
    className = 'header'
    >
    <p
      className="logo"
      css={{
        fontSize: "2rem",
        margin: "0"
      }}
    >
      My Blog
    </p>
    <div
      className="links"
      css={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        width: "40%",
        marginLeft: "auto"
      }}
    >
      <Link href="/post/new" passHref>
        <a>Add Post</a>
      </Link>
      <p>Recipe</p>
      <p>About Me</p>
      <p>Contact</p>
    </div>
  </header>
  </>
);

